<?session_start();include $_SERVER['DOCUMENT_ROOT'] . "/common/conf/config.inc.php";include $_SERVER['DOCUMENT_ROOT'] . "/backoffice/auth/auth.php";include $_SERVER['DOCUMENT_ROOT'] . "/backoffice/module/admin/admin.lib.php";if(!in_array("admin_manage",$_SESSION[$_SITE["DOMAIN"]]["ADMIN"]["AUTH"]) && $_SESSION[$_SITE["DOMAIN"]]["ADMIN"]["GRADE"]!="ROOT"):	jsMsg("권한이 없습니다.");	jsHistory("-1");endif;if(isset($_REQUEST['idx'])){	$idx = $_REQUEST['idx'];}else{	$idx = "";}if($_POST['evnMode']=="createAdmin"){	//DB연결	$dblink = SetConn($_conf_db["main_db"]);	$RS = inputAdmin();	if($RS==true){		jsGo("admin.php","","");	}else{		jsMsg("관리자 등록에 실패 하였습니다.");		jsHistory("-1") ;	}	//DB해제	SetDisConn($dblink);}else if($_POST['evnMode']=="updateAdmin"){	//DB연결	$dblink = SetConn($_conf_db["main_db"]);	$RS = updateAdmin($idx);	if($RS==true){		jsGo("admin.php","","");	}else{		jsMsg("관리자 수정에 실패 하였습니다.");		jsHistory("-1") ;	}	//DB해제	SetDisConn($dblink);}else if($_POST['evnMode']=="deleteAdmin"){	//DB연결	$dblink = SetConn($_conf_db["main_db"]);	$RS = deleteAdmin($idx);	if($RS==true){		jsGo("admin.php","","");	}else{		jsMsg("관리자 삭제에 실패 하였습니다.");		jsHistory("-1") ;	}	//DB해제	SetDisConn($dblink);}else if($_POST['evnMode']=="setAdmin"){	//DB연결	$dblink = SetConn($_conf_db["main_db"]);	$RS = updateShopSet($idx);	//DB해제	SetDisConn($dblink);		$retUrl = "admin_set.php";	if($_POST['returl']){		$retUrl = $_POST['returl'];	}	if($RS==true){		jsGo($retUrl,"","저장되었습니다.");	}else{		jsMsg("기본정보설정 수정에 실패 하였습니다.");		jsHistory("-1") ;	}}else if($_POST['evnMode']=="setDiscount"){    //DB연결    $dblink = SetConn($_conf_db["main_db"]);    $RS = updateDiscount();    //DB해제    SetDisConn($dblink);    $retUrl = "admin_discount.php";    if($_POST['returl']){        $retUrl = $_POST['returl'];    }    if($RS==true){        jsGo($retUrl,"","저장되었습니다.");    }else{        jsMsg("기본정보설정 수정에 실패 하였습니다.");        jsHistory("-1") ;    }}else if($_POST['evnMode']=="setBanner"){	//DB연결	$dblink = SetConn($_conf_db["main_db"]);	$RS = updateShopBannerSet($idx);	//DB해제	SetDisConn($dblink);		$retUrl = "/backoffice/module/banner/banner.php";	if($_POST['returl']){		$retUrl = $_POST['returl'];	}	if($RS==true){		jsGo($retUrl,"","저장되었습니다.");	}else{		jsMsg("수정에 실패 하였습니다.");		jsHistory("-1") ;	}}?>