<?PHPinclude $_SERVER['DOCUMENT_ROOT'] . "/backoffice/pub/inc/admin_top.php";include "./menu.php";include_once $_SERVER ['DOCUMENT_ROOT'] . "/module/member/member.lib.php";include_once $_SERVER['DOCUMENT_ROOT'] . "/module/category/category.lib.php";include_once $_SERVER ['DOCUMENT_ROOT'] . "/module/coupon/coupon.lib.php";if (! in_array ( "member_manage", $_SESSION [$_SITE ["DOMAIN"]] ["ADMIN"] ["AUTH"] ) && $_SESSION [$_SITE ["DOMAIN"]] ["ADMIN"] ["GRADE"] != "ROOT") :	jsMsg ( "권한이 없습니다." );	jsHistory ( "-1" );endif;if ($_GET['page_size']) {	$scale = $_GET['page_size'];} elseif ($_GET['page_size'] === '0') {	$scale = 0;} else {	$scale = 20;}// DB연결$dblink = SetConn ( $_conf_db ["main_db"] );updateMemberLevelByViolation();  // 정지 회원 자동 해제$arrList = getMemberList( mysqli_real_escape_string ( $GLOBALS ['dblink'], $_REQUEST ['jb'] ),  mysqli_real_escape_string ( $GLOBALS ['dblink'], $_REQUEST ['sw'] ), mysqli_real_escape_string ( $GLOBALS ['dblink'], $_REQUEST ['sk'] ), $scale, $_REQUEST ['offset'] , "", " order by user_editdt desc, idx desc");// _DEBUG($arrList);$arrLevel = getArticleList ( $_conf_tbl ["member_level"], 0, 0, "order by level_no desc " );for($i = 0; $i < $arrLevel["total"]; $i ++) {	$arrayLevel[$arrLevel["list"][$i]['level_no']] = $arrLevel["list"][$i]['level_name'];}$arrCouponList = getCouponListAdmin(0, 0, "Y");$chDate = date('Y-m-d H:i:s',strtotime(date("Y-m-d")."-30 day"));   // 한달$arrCount = getArticleList("tbl_member", 0, 0, " where user_level>0 and user_editdt >'".$chDate."' ");//전체 카테고리 가져오기$arrAllCategory = getCategoryAll();$smsMailId = "member";$arrBoardSmsList = getBoardListBaseNFile("mailsms", "sms", "", "", $arrBoardInfo["list"][0]["scale"], $_GET['offset'], 'and etc_3="' . $smsMailId . '"');$arrBoardMailList = getBoardListBaseNFile("mailsms", "mail", "", "", $arrBoardInfo["list"][0]["scale"], $_GET['offset'], 'and etc_3="' . $smsMailId . '"');// DB해제SetDisConn ( $dblink );?><style>    .bdr_btm .btns .btn_del {width: 100px;}</style><script>// 선택 삭제시 singleSelect=true 값 변경 falsefunction getSelectionMemeber(selectedValue) {    var ss = "0";    var rows = $('input:checkbox[name=chk_list]:checked');    for (var i = 0; i < rows.length; i++) {        var row = rows[i];        ss += "," + row.value;    }    if (rows.length > 0) {        memberLevel(ss, selectedValue);    } else {        alert('선택된 항목이 없습니다.');    }}function boardDel(val){	//location.href="/backoffice/module/member/ajax_member_del.php?g_idx="+val;	//return;	//alert(val);	//return;	if(confirm("탈퇴 처리 하시겠습니까?")) {		$.post("/backoffice/module/member/ajax_member_del.php", {g_idx: val },		function(data){					//alert(data);			location.reload();		});	}}function memberLevel(idx, lval){	if(confirm("수정 하시겠습니까?")) {        $.post("/backoffice/module/member/ajax_member_level.php", {                g_idx: idx,                levelval: lval,                child_violation: $('#child_violation').val(),                child_category: lval,                child_violation_wdate: $('#child_violation_wdate').text(),                child_violation_start_date: $('#child_violation_start_date').val(),                child_violation_end_date: $('#child_violation_end_date').val()            },		function(data){					//alert(data);			location.reload();		});	}}function userCoupon(uid, idx){	if(confirm("해당 회원에게 쿠폰을 발행합니다. 계속 하시겠습니까?")) {		$.post("/backoffice/module/coupon/coupon_evn.php", {evnMode:"user_coupon", idx: idx,user_id: uid},		function(data){					if(data=="OK"){				alert("발행되었습니다.");			}			//location.reload();		});	}}function delMember(id){	var cfm;	cfm =false;	cfm = confirm(id + " 이 회원을 탈퇴처리 하시겠습니까?\n\n탈퇴처리시 복구 불가능합니다.");	if(cfm==true){		document.frmContentsHidden.user_id.value = id;		document.frmContentsHidden.submit();	}} // 선택 삭제시 singleSelect=true 값 변경 false function getSelections(action) {     var ss = "0";     var rows = $('input:checkbox[name=chk_list]:checked');     // Collect selected IDs     for(var i=0; i<rows.length; i++){         var row = rows[i];         ss += "," + row.value;     }     if(rows.length > 0){         switch(action) {             case 'delete':                 boardDel(ss);                 break;             case 'sms':                 sendSMS(rows);                 break;             case 'email':                 sendEmail(rows);                 break;             default:                 alert('올바른 작업을 선택해주세요.');                 break;         }     } else {         alert('선택된 항목이 없습니다.');     } } // SMS sending functionfunction sendSMS(selectedRows) {    var smsTemplateId = $('select[name=sms_options]').val();    if (!smsTemplateId) {        alert('문자발송 리스트에서 발송할 내용을 선택해주세요.');        return;    }    var phones = [];    var idx = smsTemplateId;    // 선택된 행에서 전화번호 수집    $(selectedRows).each(function() {        var row = $(this).closest('tr');        var phone = row.find('td:eq(6)').text().trim(); // 연락처 컬럼        if (phone) {            phones.push(phone);        }    });    if (confirm(phones.length + '개의 번호로 문자를 발송하시겠습니까?')) {        $.ajax({            url: '/module/board/ajax_board_send_sms.php',            type: 'POST',            data: {                phones: phones.join(','),                idx: idx,                etc_3: '<?=$smsMailId?>'            },            success: function(response) {                if (response === "true") {                    alert('문자가 발송되었습니다.');                } else {                    alert('문자 발송 중 오류가 발생했습니다: ' + response.message);                }            },            error: function() {                alert('문자 발송 요청 중 오류가 발생했습니다.');            }        });    }} // Email sending function function sendEmail(selectedRows) {     var emailTemplateId = $('select[name=email_options]').val();     if (!emailTemplateId) {         alert('이메일발송 리스트에서 발송할 내용을 선택해주세요.');         return;     }     var emails = [];     var idx = emailTemplateId;     // Collect email addresses from selected rows     $(selectedRows).each(function() {         var row = $(this).closest('tr');         var email = row.data('email'); // Get email address from data attribute         if (email) {             emails.push(email);         }     });     if (confirm(emails.length + '개의 이메일로 발송하시겠습니까?')) {         $.ajax({             url: '/module/board/ajax_board_send_mail.php',             type: 'POST',             data: {                 emails: emails.join(','),                 idx: idx,                 etc_3 : '<?=$smsMailId?>'             },             success: function(response) {                 if (response === "true") {                     alert('이메일이 발송되었습니다.');                 } else {                     //alert(response);                     alert('이메일 발송 중 오류가 발생했습니다: ' + response);                 }             },             error: function() {                 alert('이메일 발송 요청 중 오류가 발생했습니다.');             }         });     } }</script><div class="container">	<div class="title">회원 리스트</div>	<form name="frmSort" method="get" action="member.php">	<div class="inbox top_search">		<!--<dl>			<dt>회원정렬</dt>			<dd>				<select name="orderby" id="" style="width:120px;" onchange="document.frmSort.submit()" >					<option value="">회원가입날짜</option>					<option value="sum_amount" <?=$_GET['orderby']=="sum_amount"?"selected":""?>>총 구매금액 순</option>					<option value="cnt_amount" <?=$_GET['orderby']=="cnt_amount"?"selected":""?>>총 구매 건수</option>					<option value="cnt_cag"  <?=$_GET['orderby']=="cnt_cag"?"selected":""?>>교환 완료</option>					<option value="cnt_cet"  <?=$_GET['orderby']=="cnt_cet"?"selected":""?>>환불 완료</option>				</select>			</dd>		</dl>-->		<!---->        <dl>            <dt>회원상태</dt>            <dd>                <select name="user_level">                    <option value="">전