<?PHPinclude $_SERVER['DOCUMENT_ROOT'] . "/backoffice/pub/inc/admin_top.php";include $_SERVER['DOCUMENT_ROOT'] . "/backoffice/module/admin/menu.php";if(!in_array("board_manage",$_SESSION[$_SITE["DOMAIN"]]["ADMIN"]["AUTH"]) && $_SESSION[$_SITE["DOMAIN"]]["ADMIN"]["GRADE"]!="ROOT"):	jsMsg("권한이 없습니다.");	jsHistory("-1");endif;?><script language="javascript">function checkform(f){	if(f.subject.value==""){		alert("제목을 입력해주세요.");		f.subject.focus();		return false;	}	if(f.s_date.value==""){		alert("팝업 시작일을 선택해 주세요.");		f.s_date.focus();		return false;	}	if(f.e_date.value==""){		alert("팝업 종료일을 선택해 주세요.");		f.e_date.focus();		return false;	}	if(f.width.value==""){		alert("팝업가로크기를 입력하세요.");		f.width.focus();		return false;	}	if(f.height.value==""){		alert("팝업세로크기를 입력하세요.");		f.height.focus();		return false;	}	if(f.p_mode[0].checked==false && f.p_mode[1].checked==false){		alert("팝업형태를 선택해 주세요.");		f.p_mode[0].focus();		return false;	}	if(f.p_type[0].checked==false && f.p_type[1].checked==false){		alert("팝업타입을 선택해 주세요.");		f.p_type[0].focus();		return false;	}	if(f.p_type[0].checked==true){		if(f.photo_file.value==""){			alert('팝업 이미지를 선택해 주세요.');			f.photo_file.focus();			return false;		}	}	try{ contents.outputBodyHTML(); } catch(e){ }}function popupCheck(fval){	f = document.frmPopup;	if(fval=="IMG"){		f.photo_file.disabled = false;		f.p_url.disabled = false;		f.p_target[0].disabled = false;		f.p_target[1].disabled = false;	}else{		f.photo_file.disabled = true;		f.p_url.disabled = true;		f.p_target[0].disabled = true;		f.p_target[1].disabled = true;	}}</script><div class="container">	<div class="title">팝업 등록</div>		<div class="inbox write_tbl mo_break_write">		<?php		$arrCategory = array(			"1" => "주안영상미디어센터",			"2" => "KCIA",			"3" => "KIMA",			"4" => "문화연예시상식"		);		?>		<form name="frmPopup" method="post" action="popup_evn.php" ENCTYPE="multipart/form-data" onsubmit="return checkform(this);">